mapper.select:
select t_student.*, u_name, class_no, class_name, enter_year, manager_user_fk from t_student left JOIN t_class ON t_student.class_fk = t_class.id left JOIN t_user ON t_class.manager_user_fk = t_user.id
where
class_fk=${classId} and (s_num like '%${keyword}%' or s_name like '%${keyword}%') order by ${orderParam1} ${orderParam2} limit ${offset}, ${pageSize}

mapper.insert:
insert into t_student (s_num, s_name, s_birthday, s_sex

, s_photo

) values (#{s_num}, #{s_name}, #{s_birthday}, #{s_sex}

 , #{s_photo}

)

mapper.update:
update t_student set s_name=#{s_name}, s_sex=#{s_sex}

, s_photo=#{s_photo} 

where s_num=#{s_num}
